%creating X and y%class pwr_val pwr_rem on/off%0 15.4%1 4%2 7%3 15.4%4 30p=[0:0.01:50]';class0=zeros(5001,2);class1=ones(5001,2);class2=ones(5001,2);class3=ones(5001,2);class4=ones(5001,2);class0(:,2)=(class0(:,2)+1)*15.4;class1(:,2)=class1(:,2)*4;class2(:,1)=class2(:,1)*2;class2(:,2)=class2(:,2)*7;class3(:,1)=class3(:,1)*3;class3(:,2)=class3(:,2)*15.4;class4(:,1)=class4(:,1)*4;class4(:,2)=class4(:,2)*30;cp0 =[class0, p];cp1 =[class1, p];cp2 =[class2, p];cp3 =[class3, p];cp4 =[class4, p];cp=[cp0', cp1', cp2', cp3', cp4']';X=cp;y0=[p>class0(:,2)];y1=[p>class1(:,2)];y2=[p>class2(:,2)];y3=[p>class3(:,2)];y4=[p>class4(:,2)];y=[y0', y1', y2', y3', y4']';pos = find(y == 1); neg = find(y == 0);plot(X(pos, 2), X(pos, 3), 'k+','LineWidth', 1, 'MarkerSize', 10)hold onplot(X(neg, 2), X(neg, 3), 'ko', 'MarkerFaceColor', 'y', 'MarkerSize', 5)hold offC = 1;model = svmTrain(X, y, C, @linearKernel, 1e-3, 20);w = model.w;b = model.b;xp = linspace(min(X(:,2)), max(X(:,2)), 100);yp = - (w(1)*xp + b)/w(2);pos = find(y == 1); neg = find(y == 0);plot(X(pos, 2), X(pos, 3), 'k+','LineWidth', 1, 'MarkerSize', 10)hold onplot(X(neg, 2), X(neg, 3), 'ko', 'MarkerFaceColor', 'y', 'MarkerSize', 5)hold offhold on;plot(xp, yp, '-b'); hold off